mapper.insert:

	SELECT MP_BOARD_SEQ.NEXTVAL
	FROM DUAL

INSERT INTO MP_BOARD (
	BNO
	, TITLE
	, CONTENT
	, WRITER
) VALUES (
	#{bno}
	, #{title}
	, #{content}
	, #{writer}
)

mapper.update:
UPDATE MP_BOARD SET
	TITLE = #{title}
	, CONTENT = #{content}
WHERE BNO = #{bno}

mapper.insert:
INSERT INTO MP_FILE (
	FILE_NO
	, BNO
	, ORG_FILE_NAME
	, STORED_FILE_NAME
	, FILE_SIZE
) VALUES (
	SEQ_MP_FILE_NO.NEXTVAL
	, #{BNO}
	, #{ORG_FILE_NAME}
	, #{STORED_FILE_NAME}
	, #{FILE_SIZE}
)

mapper.update:
UPDATE MP_FILE SET
	DEL_GB = 'Y'
WHERE FILE_NO = #{FILE_NO}

UPDATE MP_BOARD SET
	HIT = HIT + 1
WHERE BNO = #{bno}
